import org.marker.ext.ModuleActivatorimport org.marker.ext.ModuleUtilsimport org.marker.ext.module.ModuleContextimport org.marker.mushroom.ext.menu.MenuUtilsimport org.marker.mushroom.ext.model.ContentModelContextimport org.marker.mushroom.ext.model.impl.LoanModelImpl/** * 自定义模块 *  * @author marker *  */public class ModuleActivatorImpl extends ModuleUtils implements ModuleActivator {		 	public void start(ModuleContext context) throws Exception {		ContentModelContext.getInstance().put(new LoanModelImpl());		MenuUtils menuUtils = MenuUtils.getInstance();		// 人力资源管理菜单		def menu = [				name: "贷款管理",				url : "loan/list.do",				desc: "系统贷款内容管理",				icon: "fa-th",				type: "07c8c84aba8442f1bca19b3eac9b6c7a" // 唯一编码		];		this.id = menuUtils.build(MenuUtils.L1_CONTENT, menu);	} 				public void stop(ModuleContext context) throws Exception {		ContentModelContext.getInstance().remove("loan");		MenuUtils menuUtils = MenuUtils.getInstance();		menuUtils.remove(this.id);			}}