import org.marker.ext.ModuleActivatorimport org.marker.ext.ModuleUtilsimport org.marker.ext.module.ModuleContextimport org.marker.mushroom.ext.menu.MenuUtilsimport org.marker.mushroom.ext.model.ContentModelContextimport org.marker.mushroom.ext.model.impl.InvestorModelImpl/** * 自定义模块 *  * @author marker *  */public class ModuleActivatorImpl extends ModuleUtils implements ModuleActivator {		 	public void start(ModuleContext context) throws Exception {		ContentModelContext.getInstance().put(new InvestorModelImpl());		MenuUtils menuUtils = MenuUtils.getInstance();		// 人力资源管理菜单		def menu = [				name: "投资商管理",				url : "investor/list.do",				desc: "系统投资商内容管理",				icon: "fa-th",				type: "1902d62551c84b2199a505bade2873d5" // 唯一编码		];		this.id = menuUtils.build(MenuUtils.L1_CONTENT, menu);			} 				public void stop(ModuleContext context) throws Exception {		ContentModelContext.getInstance().remove("investor");		MenuUtils menuUtils = MenuUtils.getInstance();		menuUtils.remove(this.id);			}}