import org.marker.ext.ModuleActivatorimport org.marker.ext.ModuleUtilsimport org.marker.ext.module.ModuleContextimport org.marker.mushroom.ext.menu.MenuUtilsimport org.marker.mushroom.ext.model.ContentModelContextimport org.marker.mushroom.ext.model.impl.HumanResourcesModelImpl/** * 自定义模块 *  * @author marker *  */public class ModuleActivatorImpl extends ModuleUtils implements ModuleActivator {		 	public void start(ModuleContext context) throws Exception {		ContentModelContext.getInstance().put(new HumanResourcesModelImpl());		MenuUtils menuUtils = MenuUtils.getInstance();		// 人力资源管理菜单		def menu = [				name: "服务管理",				url : "humanResources/list.do",				desc: "系统基础内容管理",				icon: "fa-th",				type: "2f5b6509cd714bf6b144611f774fa824" // 唯一编码		];		this.id = menuUtils.build(MenuUtils.L1_CONTENT, menu);			} 				public void stop(ModuleContext context) throws Exception {		ContentModelContext.getInstance().remove("humanResources");		MenuUtils menuUtils = MenuUtils.getInstance();		menuUtils.remove(this.id);			}}