import org.marker.ext.ModuleActivatorimport org.marker.ext.ModuleUtilsimport org.marker.ext.module.ModuleContextimport org.marker.mushroom.ext.model.ContentModelContextimport org.marker.mushroom.ext.model.impl.ProductModelImpl/** * 自定义模块 * * @author marker * */public class ModuleActivatorImpl extends ModuleUtils implements ModuleActivator {	public void start(ModuleContext context) throws Exception {		ContentModelContext.getInstance().put(new ProductModelImpl());	}	public void stop(ModuleContext context) throws Exception {		ContentModelContext.getInstance().remove("product");	}}