import org.marker.ext.ModuleActivatorimport org.marker.ext.ModuleUtilsimport org.marker.ext.module.ModuleContextimport org.marker.mushroom.ext.menu.MenuUtilsimport org.marker.mushroom.ext.model.ContentModelContextimport org.marker.mushroom.ext.model.impl.IncubatorModelImpl/** * 自定义模块 *  * @author marker *  */public class ModuleActivatorImpl extends ModuleUtils implements ModuleActivator {		 	public void start(ModuleContext context) throws Exception {		ContentModelContext.getInstance().put(new IncubatorModelImpl());		MenuUtils menuUtils = MenuUtils.getInstance();		// 文章菜单		def menu = [				name: "孵化器管理",				url : "incubator/list.do",				desc: "系统基础内容管理",				icon: "fa-th",				type: "ad9d1d82bfe44ec19d90098f4315a047" // 唯一编码		];		this.id = menuUtils.build(MenuUtils.L1_CONTENT, menu);	} 				public void stop(ModuleContext context) throws Exception {		ContentModelContext.getInstance().remove("incubator");		MenuUtils menuUtils = MenuUtils.getInstance();		menuUtils.remove(this.id);	}}