import org.marker.ext.ModuleActivatorimport org.marker.ext.ModuleUtilsimport org.marker.ext.module.ModuleContextimport org.marker.mushroom.ext.menu.MenuUtilsimport org.marker.mushroom.ext.model.ContentModelContextimport org.marker.mushroom.ext.model.impl.ActivityModelImpl/** * 自定义模块 *  * @author marker *  */public class ModuleActivatorImpl extends ModuleUtils implements ModuleActivator {		 	public void start(ModuleContext context) throws Exception {		ContentModelContext.getInstance().put(new ActivityModelImpl());		MenuUtils menuUtils = MenuUtils.getInstance();		// 文章菜单		def menu = [				name: "活动管理",				url : "activity/list.do",				desc: "系统基础内容管理",				icon: "fa-th",				type: "d268accf105d47c7a1db3a3d4e8a1d93" // 唯一编码		];		this.id = menuUtils.build(MenuUtils.L1_CONTENT, menu);			} 				public void stop(ModuleContext context) throws Exception {		ContentModelContext.getInstance().remove("activity");		MenuUtils menuUtils = MenuUtils.getInstance();		menuUtils.remove(this.id);			}}